actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetProfileService = $injector.get(widgetContext.servicesMap.get("assetProfileService"));
let attributeService = $injector.get(widgetContext.servicesMap.get("attributeService"));
let entityRelationService = $injector.get(widgetContext.servicesMap.get("entityRelationService"));
let assetService = $injector.get(widgetContext.servicesMap.get("assetService"));

openAssetDialog();

function openAssetDialog() {
    customDialog.customDialog(htmlTemplate, AssetEditDialogController).subscribe();
}

function AssetEditDialogController(instance) {
    let vm = instance;

    vm.locationOptions = [];
    vm.deviceOptions = [];
    vm.filteredDeviceOptions = [];

    const preAssignedDevice = additionalParams.entity["Assigned device"] ? JSON.parse(additionalParams.entity["Assigned device"]) : null;
    const preAssignedLocation = additionalParams.entity["Assigned location"] ? JSON.parse(additionalParams.entity["Assigned location"]) : null;

    function findAndUpdateAssetType(assetTypesList, type) {
        const selectedAssetType = assetTypesList.find((assetType) => assetType.name === type);
        if (selectedAssetType) {
            vm.settingsFormGroup.get("type").setValue(selectedAssetType.id);
        }
    }

    vm.assetInfoFormGroup = vm.fb.group({
        name: [entityName, [vm.validators.required, vm.validators.maxLength(40)]],
        description: [additionalParams.entity.description],
        modelNumber: [additionalParams.entity.modelNumber],
        serialNumber: [additionalParams.entity.serialNumber]
    });

    vm.settingsFormGroup = vm.fb.group({ 
        type: [null, [vm.validators.required]],
        assignedDevice: [preAssignedDevice ? preAssignedDevice.name : null],
        assignedLocation: [preAssignedLocation],
        department: [additionalParams.entity.Department]
    });

    vm.imageFormGroup = vm.fb.group({
        image: [additionalParams.entity.image, []]
    });

    vm.settingsFormGroup.get("assignedDevice").valueChanges.subscribe(value=>{
        const filterValue = value.toLowerCase();

        vm.filteredDeviceOptions = vm.deviceOptions.filter(option => option.name.toLowerCase().includes(filterValue));
    });

    const pageLink = widgetContext.pageLink(100000);

    const assetTypes = ["Loader", "Box", "Worker", "Tool"];

    assetService.getTenantAssets(pageLink, 'Location').subscribe(locations => {
        if(locations && locations.data){
            locations.data.forEach(x=>{
                vm.locationOptions.push({
                    name: x.name,
                    id: x.id
                });
            });
        }
    });

    vm.assetTypeOptions = [];
    assetProfileService.getAssetProfiles(pageLink).subscribe(response => {
        response.data.forEach((assetProfile) => {
            if (assetTypes.includes(assetProfile.name)) {
                vm.assetTypeOptions.push({
                    id: assetProfile.id,
                    name: assetProfile.name
                });
            }
        });

        widgetContext.$scope.assetTypeOptions = vm.assetTypeOptions;
        findAndUpdateAssetType(vm.assetTypeOptions, additionalParams.entity.Type); 
        widgetContext.detectChanges();
    });

    const queryParams = {
        entityFilter: {
            type: "deviceType",
            deviceType: "Tag",
        },
        entityFields: [
            {
                type: "ENTITY_FIELD",
                key: "name"
            }
        ],
        latestValues: [
            {
                type: "ATTRIBUTE",
                key: "hasRelatedAsset"
            }    
        ],
        pageLink
    };

    widgetContext.http.post(`/api/entitiesQuery/find`, queryParams)
        .subscribe(response => {
            response.data.forEach(device => {
                if(device.latest.ATTRIBUTE.hasRelatedAsset.value == "" || device.latest.ATTRIBUTE.hasRelatedAsset.value == "false"){
                    vm.deviceOptions.push({
                        id: device.entityId,
                        name: device.latest.ENTITY_FIELD.name.value
                    });
                }
            });

            if(preAssignedDevice) {
                vm.deviceOptions.push(preAssignedDevice);
            }

            vm.filteredDeviceOptions = vm.deviceOptions;

            widgetContext.detectChanges();
        });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.compareFn = function(c1, c2) {
        return c1 && c2 ? c1.id.id === c2.id.id : c1 === c2;
    }

    vm.save = function() {
        const assetInfo = vm.assetInfoFormGroup.value;
        const assetSettings = vm.settingsFormGroup.value;

        const hasRelatedDevice = !!assetSettings.assignedDevice;
        const hasRelatedLocation = !!assetSettings.assignedLocation;

        const updateAssetPayload = {
            id: entityId,
            additionalInfo: {
                description: assetInfo.description
            },
            name: assetInfo.name,
            assetProfileId: assetSettings.type
        };

        const updateAssetAttributes = [
            {
                key: "image",
                value: vm.imageFormGroup.value.image
            },
            {
                key: "serialNumber",
                value: assetInfo.serialNumber                
            },
            {
                key: "modelNumber",
                value: assetInfo.modelNumber
            },
            {
                key: "department",
                value: assetSettings.department
            },
            {
                key: "assignedDevice",
                value: vm.deviceOptions.find(x=>x.name == assetSettings.assignedDevice)
            },
            {
                key: "assignedLocation",
                value: assetSettings.assignedLocation
            },
            {
                key: "hasRelatedDevice",
                value: hasRelatedDevice
            },
            {
                key: "hasRelatedLocation",
                value: hasRelatedLocation
            },
            {
                key: "description",
                value: assetInfo.description
            }
        ];

        const forkedArr = [
            // update asset main info (name and description)
            assetService.saveAsset(updateAssetPayload),
            // update asset attributes
            attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", updateAssetAttributes),
        ];

        const removeOldDeviceAttrAndRelation = () => {
            const oldDeviceAttributes = [
                {
                    key: "hasRelatedAsset",
                    value: false
                },
                {
                    key: "assignedAsset",
                    value: null
                },
                {
                    key: "assignedLocation",
                    value: null
                }
            ]; 

            // set hasRelatedAsset attr for old device to false
            forkedArr.push(attributeService.saveEntityAttributes(preAssignedDevice.id, "SERVER_SCOPE", oldDeviceAttributes));

            // delete relation from asset to old device
            forkedArr.push(entityRelationService.deleteRelation(entityId, "DeviceToAsset", preAssignedDevice.id));
        };

        const removeOldLocationRelation = () => {
            // delete relation from asset to old location
            forkedArr.push(entityRelationService.deleteRelation(preAssignedLocation.id, "LocationToAsset", entityId));
        };

        const addNewLocationRelation = () => {
            const newRelation = {
                from: assetSettings.assignedLocation.id,
                to: entityId,
                type: "LocationToAsset",
                typeGroup: "COMMON"
            };

            // create relation from asset to new assigned device
            forkedArr.push(entityRelationService.saveRelation(newRelation));
        };

        const addNewDeviceAttrAndRelation = () => {
            const newDeviceAttributes = [
                {
                    key: "hasRelatedAsset",
                    value: true
                },
                {
                    key: "assignedAsset",
                    value: JSON.stringify({
                        name: assetInfo.name,
                        id: entityId
                    })
                }
            ];

            // set hasRelatedAsset attr for new device to true
            forkedArr.push(attributeService.saveEntityAttributes(vm.deviceOptions.find(x=>x.name == assetSettings.assignedDevice).id, "SERVER_SCOPE", newDeviceAttributes));

            const newRelation = {
                from: entityId,
                to: vm.deviceOptions.find(x=>x.name == assetSettings.assignedDevice).id,
                type: "DeviceToAsset",
                typeGroup: "COMMON"
            };

            // create relation from asset to new assigned device
            forkedArr.push(entityRelationService.saveRelation(newRelation));
        };

        const updateDeviceAttr = () => {
            const updatedDeviceAttributes = [
                {
                    key: "assignedAsset",
                    value: JSON.stringify({
                        name: assetInfo.name,
                        id: entityId
                    })
                }
            ];

            forkedArr.push(attributeService.saveEntityAttributes(vm.deviceOptions.find(x=>x.name == assetSettings.assignedDevice).id, "SERVER_SCOPE", updatedDeviceAttributes));
        };

        if(hasRelatedDevice){
            if (preAssignedDevice) {
                const isSameDevice = JSON.stringify(preAssignedDevice) === JSON.stringify(vm.deviceOptions.find(x=>x.name == assetSettings.assignedDevice));

                if (!isSameDevice) {
                    removeOldDeviceAttrAndRelation();
                    addNewDeviceAttrAndRelation();
                } else {
                    updateDeviceAttr();
                }
            } else {
                addNewDeviceAttrAndRelation();
            }
        } else {
            if(preAssignedDevice){
                removeOldDeviceAttrAndRelation();
            }
        }

        if(hasRelatedLocation){
            if(preAssignedLocation){
                const isSameLocation = JSON.stringify(preAssignedLocation) === JSON.stringify(assetSettings.assignedLocation);

                if (!isSameLocation) {
                    removeOldLocationRelation();
                    addNewLocationRelation();
                }
            } else {
                addNewLocationRelation();
            }
        } else {
            if(preAssignedLocation){
                removeOldLocationRelation();
            }
        }

        widgetContext.rxjs.forkJoin(forkedArr).subscribe(() => {
            widgetContext.updateAliases();
            vm.dialogRef.close(null);
            widgetContext.showSuccessToast(`${assetInfo.name} asset was updated successfully!`, 4000, "bottom", "left", "layout");
        });
    };
}

