let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let http = $injector.get(widgetContext.servicesMap.get('http'));

openDeleteAssetWindow();

function openDeleteAssetWindow() {
    customDialog.customDialog(htmlTemplate, DeleteAssetDialogController).subscribe();
}

function DeleteAssetDialogController(instance) {
    let vm = instance;

    vm.entityName = entityName;
    vm.locationId = null;

    if(additionalParams.entity["Assigned location"] && JSON.parse(additionalParams.entity["Assigned location"]).id){
        vm.locationId = JSON.parse(additionalParams.entity["Assigned location"]).id;
    }

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.delete = function() {
        const requests = [];

        if(vm.locationId){
            requests.push(entityRelationService.deleteRelation(vm.locationId, "LocationToAsset", entityId));
        }

        requests.push(assetService.deleteAsset(entityId.id));

        try {
            if(additionalParams.entity["Assigned device"]){
                const preAssignedDevice = JSON.parse(additionalParams.entity["Assigned device"]);
                const deviceAttributes = [
                    {
                        key: "hasRelatedAsset",
                        value: false
                    },
                    {
                        key: "assignedAsset",
                        value: null
                    },
                    {
                        key: "assignedLocation",
                        value: null
                    }
                ];
                requests.push(attributeService.saveEntityAttributes(preAssignedDevice.id, "SERVER_SCOPE", deviceAttributes));
            }
        } catch(err) {}

        widgetContext.rxjs.forkJoin(requests).subscribe(()=>{
            widgetContext.showSuccessToast(`"${entityName}" asset was successfully deleteted!`, 4000, "bottom", "left", "layout");
            widgetContext.updateAliases();
            vm.dialogRef.close(null);
        });
    };
}

