let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));  
let attributeService = $injector.get(widgetContext.servicesMap.get("attributeService"));
let assetService = $injector.get(widgetContext.servicesMap.get("assetService"));

openUserDialog();

function openUserDialog() {
    customDialog.customDialog(htmlTemplate, CreateUserDialogController).subscribe();
}

function CreateUserDialogController(instance) {
    let vm = instance;

    vm.types = ["Tag", "Anchor"];
    vm.connectivities = ["RFID (MQQT)"];

    vm.deviceConfigForm = vm.fb.group({
        name: ["", [vm.validators.required, vm.validators.maxLength(40)]],
        description: [""],
        type: ["", [vm.validators.required]],
        connectivity: [""],
        frequency: [""],
        firmware: [""]
    });

    getEntityInfo();

    function getEntityInfo(){
        vm.deviceConfigForm.patchValue({
            name: additionalParams.entity["Name"],
            description: additionalParams.entity["Description"],
            type: additionalParams.entity["Device type"],
            connectivity: additionalParams.entity["Device connectivity"],
            frequency: additionalParams.entity["frequency"],
            firmware: additionalParams.entity["firmware"]
        });    
    }

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        let {name, description, type, connectivity, frequency, firmware}  = vm.deviceConfigForm.value;

        let deviceConfigPayload = {
            id: entityId,
            name,
            type: "deviceConfig"
        };

        let attributes = [];

        if(type == "Tag"){
            attributes = [{
                key: "connectivity",
                value: connectivity
            }, {
                key: "frequency",
                value: frequency
            }, {
                key: "firmware",
                value: firmware
            }];
        }

        attributes.push({
            key: "type",
            value: type
        });

        attributes.push({
            key: "description",
            value: description
        });

        assetService.saveAsset(deviceConfigPayload)
            .pipe(
                widgetContext.rxjs.switchMap((response) => {
                    return attributeService.saveEntityAttributes(response.id, "SERVER_SCOPE", attributes);
                })
            )
            .subscribe(() => {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
                widgetContext.showSuccessToast(`Device config was edited successfully!`, 4000, "bottom", "left", "layout");
            });
    }
}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));

openDeleteUserDialog();

function openDeleteUserDialog() {
    let title = `Delete device config`;
    let content = `Are you sure you want to delete the device config?`;
    dialogs.confirm(title, content, 'Cancel', 'Delete')
        .pipe(
            widgetContext.rxjs.switchMap((data) => {
                if (data) {
                    return deleteUser(); 
                } else {
                    return widgetContext.rxjs.of(null);
                }
            })
        )
        .subscribe();
}

function deleteUser() {
    return assetService.deleteAsset(entityId.id)
        .pipe(
            widgetContext.rxjs.tap(() => {
                widgetContext.showSuccessToast(`${entityName} was deleted successfully!`, 4000, "bottom", "left", "layout");
                widgetContext.updateAliases();
            }
        ));
}

