let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));  
let attributeService = $injector.get(widgetContext.servicesMap.get("attributeService"));
let assetService = $injector.get(widgetContext.servicesMap.get("assetService"));
let deviceService = $injector.get(widgetContext.servicesMap.get("deviceService"));
let userService = $injector.get(widgetContext.servicesMap.get("userService"));
let entityRelationService = $injector.get(widgetContext.servicesMap.get("entityRelationService"));

opendeviceConfigDialog();

function opendeviceConfigDialog() {
    customDialog.customDialog(htmlTemplate, CreateDeviceConfigDialogController).subscribe();
}

function CreateDeviceConfigDialogController(instance) {
    let vm = instance;

    let initialConfig;

    if(additionalParams.entity.alarmConfig && JSON.parse(additionalParams.entity.alarmConfig)){
        initialConfig = JSON.parse(additionalParams.entity.alarmConfig);
    }

    const pageLink = widgetContext.pageLink(1073741824);

    vm.devices = [];
    vm.users = [];

    let initialDevices;

    const queryParams = {
        entityFilter: {
            type: "entityType",
            entityType: "DEVICE",
        },
        entityFields: [
            {
                type: "ENTITY_FIELD",
                key: "name"
            }
        ],
        latestValues: [
            {
                type: "ATTRIBUTE",
                key: "assignedAlarmProfile"
            }
        ],
        pageLink
    };

    const requests = [
        deviceService.getTenantDevices(widgetContext.pageLink(10000,0)),
        widgetContext.http.post(`/api/entitiesQuery/find`, queryParams),
        entityRelationService.findByFromAndType(entityId, "AlarmConfigToDevice")
    ];

    widgetContext.rxjs.forkJoin(requests).subscribe(data=>{
        let allDevices = data[0];
        let busyDevices = [];
        let relatedDevices = data[2].length ? data[2].map(x=>({id: x.to})) : [];

        data[1].data.forEach(device => {
            if(device.latest.ATTRIBUTE.assignedAlarmProfile.value == "true"){
                busyDevices.push({
                    id: device.entityId,
                    name: device.latest.ENTITY_FIELD.name.value
                });
            }
        });

        vm.alarmInfoFormGroup.patchValue({
            devices: relatedDevices
        })

        initialDevices = relatedDevices;

        const devicesToDel = deleteObjectsById(busyDevices, relatedDevices);

        vm.devices = deleteObjectsById(allDevices.data, devicesToDel)
    });

    getRuleInfo();

    function getRuleInfo(){
        attributeService.getEntityAttributes(entityId, 'SERVER_SCOPE').subscribe((data)=>{
            data.forEach(x=>{
                if(x.key == "alarmConfig"){
                    vm.alarmInfoFormGroup.patchValue({
                        description: x.value.description,
                        isActive: x.value.isActive
                    });
                    vm.alarmUsersFormGroup.patchValue({
                        phone: x.value.phone,
                        application: x.value.application,
                        email: x.value.email
                    });
                }
            });
        });
    }

    userService.getAllUserInfos(true, widgetContext.pageLink(10000,0)).subscribe(users => {
        if(users && users.data && users.data.length){
            users.data.forEach(x=>{
                vm.users.push(x);
            });
        }
        entityRelationService.findByFromAndType(entityId, "AlarmConfigToInformingUser").subscribe(res=>{
            if(res && res.length){
                vm.alarmUsersFormGroup.patchValue({
                    users: res.map(x => ({id: x.to}))
                })
            }
        })
    });

    vm.compareFn = function(c1, c2) {
        return c1 && c2 ? c1.id.id === c2.id.id : c1 === c2;
    }

    vm.alarmInfoFormGroup = vm.fb.group({
        name: [entityName, [vm.validators.maxLength(40)]],
        description: [null],
        isActive: false,
        devices: [null]
    });

    vm.alarmUsersFormGroup = vm.fb.group({
        users: [null],
        phone: false,
        email: false,
        application: false
    });

    function deleteObjectsById(mainArray, idsToRemove) {
        const idsToDelete = new Set(idsToRemove.map(item => item.id.id));

        return mainArray.filter(item => !idsToDelete.has(item.id.id));
    }

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        let {name, description, isActive, devices} = vm.alarmInfoFormGroup.value;
        let {users, phone, email, application} = vm.alarmUsersFormGroup.value;

        let payload = {
            id: entityId,
            name,
            type: "Alarm config"
        };

        initialConfig.description = description;
        initialConfig.isActive = isActive;
        initialConfig.phone = phone;
        initialConfig.email = email;
        initialConfig.application = application;

        entityRelationService.deleteRelations(entityId).pipe(
            widgetContext.rxjs.switchMap(() => {
                if(initialDevices && initialDevices.length) {
                    let requests = [];

                    initialDevices.forEach(device=>{
                        requests.push(attributeService.saveEntityAttributes(device.id, 'SERVER_SCOPE', [{ key: "assignedAlarmProfile", value: false }]));    
                    })

                    return widgetContext.rxjs.forkJoin(requests);
                } else return widgetContext.rxjs.of([]);
            })    
        ).subscribe(()=>{
            assetService.saveAsset(payload).pipe(
                widgetContext.rxjs.switchMap(alertProfile => 
                    attributeService.saveEntityAttributes(alertProfile.id, "SERVER_SCOPE", [{ key: "alarmConfig", value: initialConfig }]).pipe(
                        widgetContext.rxjs.switchMap(() => {
                            const deviceRelations = devices.map(device => 
                                entityRelationService.saveRelation({
                                    from: alertProfile.id,
                                    to: device.id,
                                    type: 'AlarmConfigToDevice',
                                    typeGroup: 'COMMON'
                                }).pipe(
                                    widgetContext.rxjs.switchMap(() => 
                                        attributeService.saveEntityAttributes(device.id, 'SERVER_SCOPE', [{ key: "assignedAlarmProfile", value: true }])
                                    )
                                )
                            );

                            const userRelations = users.map(user =>
                                entityRelationService.saveRelation({
                                    from: alertProfile.id,
                                    to: user.id,
                                    type: 'AlarmConfigToInformingUser',
                                    typeGroup: 'COMMON'
                                })
                            );

                            return widgetContext.rxjs.forkJoin([...deviceRelations, ...userRelations]);
                        })
                    )
                )
            ).subscribe(()=>{
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
                widgetContext.showSuccessToast(`Alert rule was updated successfully!`, 4000, "bottom", "left", "layout");
            })
        });
    };
}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openDeleteUserDialog();

function openDeleteUserDialog() {
    let title = `Delete alert rule`;
    let content = `Are you sure you want to delete the alert rule?`;
    dialogs.confirm(title, content, 'Cancel', 'Delete')
        .pipe(
            widgetContext.rxjs.switchMap((data) => {
                if (data) {
                    deleteAlarmConfig(); 
                } else {
                    return widgetContext.rxjs.of(null);
                }
            })
        )
        .subscribe();
}

function deleteAlarmConfig() {
    entityRelationService.findByFromAndType(entityId, "AlarmConfigToDevice")
        .pipe(
            widgetContext.rxjs.switchMap((devices) => {
                let requests = [assetService.deleteAsset(entityId.id)];
                if(devices && devices.length){
                    devices.forEach(x=>{
                        requests.push(attributeService.saveEntityAttributes(x.to, 'SERVER_SCOPE', [{key: "assignedAlarmProfile", value: false}]));
                    })
                }
                return widgetContext.rxjs.forkJoin(requests);    
            })
        ).subscribe(()=>{
            widgetContext.showSuccessToast(`${entityName} was deleted successfully!`, 4000, "bottom", "left", "layout");
            widgetContext.updateAliases();
        });
}

