let params = controller.getStateParams();
let states = controller.stateObject.length;

if(states>1){
    controller.navigatePrevState(0);
}

let newParams = {
    stateId: 'alertsRuleDetails',
    prevState: 'alertsRules',
    subState: 'device',
    mode: params['mode'],
    enabled: params['enabled'],
    statesArray: [
        {
            stateId: 'alertsRules', 
            name: "Alerts Rules"
        },
        {
            stateId: 'alertsRuleDetails', 
            name: entityLabel||entityName
        }
    ],
    selectedEntity: {
        entityName,
        entityId,
        entityLabel
    }
}

history.pushState({}, '');

widgetContext.updateAliases();
controller.updateState('default', newParams)

