let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let params = widgetContext.stateController.getStateParams();

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.type = additionalParams.type;

    vm.editForm = vm.fb.group({
        numberObject: vm.fb.group({
            type: [null],
            status: [null],
            upperThreshold: [null],
            lowerThreshold: [null],
            equalThreshold: [null]
        }),
        durationObject: vm.fb.group({
            type: [null],
            status: [null],
            durationThreshold: [null, [vm.validators.pattern(/^-?[0-9]+$/)]]
        })
    });

    vm.editForm.get("numberObject.lowerThreshold").valueChanges.subscribe(lowerThreshold => {
        const upperThresholdControl = vm.editForm.get("numberObject.upperThreshold");

        if(lowerThreshold){
            upperThresholdControl.setValidators([
                vm.validators.min(lowerThreshold + 1)
            ]);
        } else {
            upperThresholdControl.setValidators(null);
        }

        upperThresholdControl.updateValueAndValidity();
    });

    vm.editForm.get("numberObject.upperThreshold").valueChanges.subscribe(upperThreshold => {
        const lowerThresholdControl = vm.editForm.get("numberObject.lowerThreshold");

        if(upperThreshold){
            lowerThresholdControl.setValidators([
                vm.validators.max(upperThreshold - 1)
            ]);
        } else {
            lowerThresholdControl.setValidators(null);
        }

        lowerThresholdControl.updateValueAndValidity();
    });

    getEntityInfo();

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.save = function() {
        let values;

        if(vm.type == "Temperature" || vm.type == "Battery level"){
            values = vm.editForm.value.numberObject; 
        } else {
            values = vm.editForm.value.durationObject; 
        }

        const index = widgetContext.$scope.datasource.findIndex(x => x.type === values.type);

        if (index != -1) {
            widgetContext.$scope.datasource[index] = values;
        }

        attributeService.saveEntityAttributes(params.selectedEntity.entityId, "SERVER_SCOPE", [{key: "alarmConfig", value: widgetContext.$scope.alarmConfig}]).subscribe(()=>{
            widgetContext.updateAliases();
            vm.dialogRef.close(null);
        });
    };

    function getEntityInfo() {
        if(vm.type == "Temperature" || vm.type == "Battery level"){
            vm.editForm.patchValue({
                numberObject: additionalParams
            });
        } else {
            vm.editForm.patchValue({
                durationObject: additionalParams
            });
        }
    }
}

