let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let utils = $injector.get(widgetContext.servicesMap.get('utils'));
let params = widgetContext.stateController.getStateParams();

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.type = additionalParams.type;
    vm.eventTypes = ["Zone In", "Zone Out", "Geofence In", "Geofence Out"];
    vm.locations = [];
    vm.zones = [];
    vm.geofences = [];
    vm.isMultiple = true;
    vm.zonesEmpty = false;
    vm.geofencesEmpty = false;

    assetService.getTenantAssets(widgetContext.pageLink(1000,0), 'Location').subscribe(locations => {
        if(locations && locations.data){
            locations.data.forEach(x=>{
                vm.locations.push({
                    name: x.name,
                    id: x.id
                });
            });
        }
    });

    vm.form = vm.fb.group({
        eventType: [null],
        locations: [{ value: null, disabled: true }],
        zones: [{ value: null, disabled: true }],
        geofences: [{ value: null, disabled: true }],
        duration: [{ value: null, disabled: true }, [vm.validators.pattern(/^-?[0-9]+$/)]]
    });

    vm.select = function(type, event){
        selectedEntityId = event.value.id;

        if(type == 'location'){
            vm.form.patchValue({
                zones: ''
            });
        } else {
            vm.form.patchValue({
                geofences: ''
            });
        }

        let filters;

        if(type == 'location'){
            filters = [{
                "relationType": "ZoneToLocation",
                "entityTypes": ["ASSET"]
            }];
        } else {
            filters = [{
                "relationType": "GeofenceToZone",
                "entityTypes": ["ASSET"]
            }];
        }

        if(vm.form.value.eventType == "Geofence In" || vm.form.value.eventType == "Geofence Out" || type == "location"){
            entityService.findEntityDataByQuery({
                entityFilter: {
                    "type": "relationsQuery",
                    "resolveMultiple": true,
                    "rootStateEntity": false,
                    "stateEntityParamName": null,
                    "defaultStateEntity": null,
                    "rootEntity": selectedEntityId,
                    "direction": "TO",
                    "maxLevel": 1,
                    "filters": filters,
                    "fetchLastLevelOnly": false,
                },
                entityFields: [{
                  "type": "ENTITY_FIELD",
                  "key": "name"
                }],
                pageLink: {
                    "page": 0,
                    "pageSize": 10000,
                    "sortOrder": {
                        "key": {
                            "key": "name",
                            "type": "ENTITY_FIELD"
                        },
                        "direction": "ASC"
                    }
                }
            }).subscribe(res=>{
                if(res.data){
                    if(type == 'location'){
                        vm.zones = [];
                        res.data.forEach(zone=>{
                            if(zone.latest["ENTITY_FIELD"]["name"]['value']){
                                if(vm.form.value.eventType == "Geofence In" || vm.form.value.eventType == "Geofence Out"){
                                    vm.zones.push({
                                        name: zone.latest["ENTITY_FIELD"]["name"]['value'],
                                        id: zone.entityId
                                    })
                                } else {
                                    if(!checkZoneOccupied(vm.form.value.eventType, zone.entityId.id)){
                                        vm.zones.push({
                                            name: zone.latest["ENTITY_FIELD"]["name"]['value'],
                                            id: zone.entityId
                                        })
                                    }
                                } 
                            }
                        });
                        if(vm.zones.length == 0){
                            vm.zonesEmpty = true;
                        } else {
                            vm.zonesEmpty = false;
                        }
                        widgetContext.detectChanges();
                    } else {
                        vm.geofences = [];
                        res.data.forEach(x=>{
                            if(x.latest["ENTITY_FIELD"]["name"]['value']){
                                if(vm.form.value.eventType == "Geofence In" || vm.form.value.eventType == "Geofence Out"){
                                    if(!checkGeofenceOccupied(vm.form.value.eventType, x.entityId.id)){
                                        vm.geofences.push({
                                            name: x.latest["ENTITY_FIELD"]["name"]['value'],
                                            id: x.entityId
                                        })
                                    }
                                }
                            }
                        });
                        if(vm.geofences.length == 0){
                            vm.geofencesEmpty = true;
                        } else {
                            vm.geofencesEmpty = false;
                        }
                        widgetContext.detectChanges();
                    }
                }
                widgetContext.detectChanges();
            });
        }
    };

    vm.form.get('eventType').valueChanges.subscribe((value) => {
        vm.isMultiple = value != 'Geofence In' && value !== 'Geofence Out';
        vm.form.patchValue({
            locations: null,
            zones: null,
            geofences: null,
            duration: null
        });
        vm.zonesEmpty = false;        
        vm.geofencesEmpty = false;

        vm.form.get('locations').enable();
        vm.form.get('zones').enable();
        vm.form.get('geofences').enable();
        vm.form.get('duration').enable();
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function checkZoneOccupied(eventType, zoneId){
        return widgetContext.$scope.datasource.some(item => 
            item.eventType === eventType &&
            Array.isArray(item.zones) && 
            item.zones.some(zone => zone.id && zone.id.id === zoneId));
    }

    function checkGeofenceOccupied(eventType, geofenceId){
        return widgetContext.$scope.datasource.some(item => 
            item.eventType === eventType &&
            Array.isArray(item.geofences) && 
            item.geofences.some(geofence => geofence.id && geofence.id.id === geofenceId)
        );
    }

    vm.save = function() {
        let values = vm.form.value;

        values.index = utils.guid(); 

        if(values.eventType){
            widgetContext.$scope.datasource.push(values);
        }

        widgetContext.$scope.alarmConfig.locationThresholds = widgetContext.$scope.datasource; 

        attributeService.saveEntityAttributes(params.selectedEntity.entityId, "SERVER_SCOPE", [{key: "alarmConfig", value: widgetContext.$scope.alarmConfig}]).subscribe(()=>{
            widgetContext.updateAliases();
            vm.dialogRef.close(null);
        });
    };
}

