let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityService = $injector.get(widgetContext.servicesMap.get('entityService'));
let utils = $injector.get(widgetContext.servicesMap.get('utils'));
let params = widgetContext.stateController.getStateParams();

openEditEntityDialog();

function openEditEntityDialog() {
    customDialog.customDialog(htmlTemplate, EditEntityDialogController).subscribe();
}

function EditEntityDialogController(instance) {
    let vm = instance;

    vm.type = additionalParams.eventType;
    vm.eventTypes = ["Zone In", "Zone Out", "Geofence In", "Geofence Out"];
    vm.locations = [];
    vm.zones = [];
    vm.geofences = [];
    vm.isMultiple = true;
    vm.zonesEmpty = false;
    vm.geofencesEmpty = false;

    assetService.getTenantAssets(widgetContext.pageLink(1000,0), 'Location').subscribe(locations => {
        if(locations && locations.data){
            locations.data.forEach(x=>{
                vm.locations.push({
                    name: x.name,
                    id: x.id
                });
            });
        }
        getRuleInfo();
    });

    vm.compareFn = function(c1, c2) {
        return c1 && c2 ? c1.id.id === c2.id.id : c1 === c2;
    }

    vm.select = function(type, event, auto){
        if(event.value.length > 0){
            selectedEntityId = event.value[0].id;
        } else {
            selectedEntityId = event.value.id;
        }

        if(type == 'location'){
            vm.form.patchValue({
                zones: null,
                geofences: null
            })
            if(!auto){
                vm.form.patchValue({
                    geofences: null
                })  
            }
        } else if(type == "getZones"){
            vm.form.patchValue({
                locations: additionalParams.locations
            })
        } else {
            vm.form.patchValue({
                geofences: null
            })
        }

        let entityFilter = {
            "type": "relationsQuery",
            "resolveMultiple": true,
            "rootStateEntity": false,
            "stateEntityParamName": null,
            "defaultStateEntity": null,
            "rootEntity": selectedEntityId,
            "fetchLastLevelOnly": false,
            "maxLevel": 1,
            "direction": "TO"
        };

        if(type == 'location' || type == "getZones"){
            entityFilter.filters = [{
                "relationType": "ZoneToLocation",
                "entityTypes": ["ASSET"]
            }];
        } else {
            entityFilter.filters = [{
                "relationType": "GeofenceToZone",
                "entityTypes": ["ASSET"]
            }];
        }

        if(vm.form.value.eventType == "Geofence In" || vm.form.value.eventType == "Geofence Out" || type == "location" || type == "getZones" || type == "getGeofences"){
            entityService.findEntityDataByQuery({
                entityFilter: entityFilter,
                entityFields: [{
                  "type": "ENTITY_FIELD",
                  "key": "name"
                }],
                pageLink: {
                    "page": 0,
                    "pageSize": 10000,
                    "sortOrder": {
                        "key": {
                            "key": "name",
                            "type": "ENTITY_FIELD"
                        },
                        "direction": "ASC"
                    }
                }
            }).subscribe(res=>{
                if(res.data){
                    if(type == 'location' || type == "getZones"){
                        vm.zones = [];
                        res.data.forEach(x=>{
                            if(x.latest["ENTITY_FIELD"]["name"]['value']){
                                if(vm.form.value.eventType == "Geofence In" || vm.form.value.eventType == "Geofence Out"){
                                    vm.zones.push({
                                        name: x.latest["ENTITY_FIELD"]["name"]['value'],
                                        id: x.entityId
                                    })
                                } else {
                                    if(!checkZoneOccupied(vm.form.value.eventType, x.entityId.id, additionalParams.zones)){
                                        vm.zones.push({
                                            name: x.latest["ENTITY_FIELD"]["name"]['value'],
                                            id: x.entityId
                                        })
                                    }
                                } 
                            }
                        });
                        if(vm.zones.length == 0){
                            vm.zonesEmpty = true;
                        } else {
                            if(auto){
                                vm.form.patchValue({
                                    zones: additionalParams.zones
                                })
                                vm.select("getGeofences", {value: additionalParams.zones}, true)
                            }
                            vm.zonesEmpty = false;
                        }
                        widgetContext.detectChanges();
                    } else {
                        vm.geofences = [];
                        res.data.forEach(x=>{
                            if(x.latest["ENTITY_FIELD"]["name"]['value']){
                                if(vm.form.value.eventType == "Geofence In" || vm.form.value.eventType == "Geofence Out"){
                                    if(!checkGeofenceOccupied(vm.form.value.eventType, x.entityId.id, additionalParams.geofences)){
                                        vm.geofences.push({
                                            name: x.latest["ENTITY_FIELD"]["name"]['value'],
                                            id: x.entityId
                                        })
                                    }
                                }
                            }
                        });
                        if(vm.geofences.length == 0){
                            vm.geofencesEmpty = true;
                        } else {
                            if(type == "getGeofences"){
                                if(auto){
                                    vm.form.patchValue({
                                        geofences: additionalParams.geofences
                                    })
                                }
                            }
                            vm.geofencesEmpty = false;
                        }
                        widgetContext.detectChanges();
                    }
                }
            });
        }
    };

    vm.form = vm.fb.group({
        index: [null],
        eventType: [null],
        locations: [null],
        zones: [null],
        geofences: [null],
        duration: [null, [vm.validators.pattern(/^-?[0-9]+$/)]]
    });

    vm.form.get('eventType').valueChanges.subscribe((value) => {
        vm.isMultiple = value != 'Geofence In' && value !== 'Geofence Out';

        vm.form.patchValue({
            locations: null,
            zones: null,
            geofences: null,
            duration: null
        });

        vm.zonesEmpty = false;        
        vm.geofencesEmpty = false;
    });

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    function checkZoneOccupied(eventType, zoneId, ignoreIds=[]){
        if(ignoreIds.length){
            ignoreIds = ignoreIds.map(x=>x.id.id)
        }

        if(eventType == vm.type){
            return widgetContext.$scope.datasource.some(item => 
                        item.eventType === eventType &&
                        Array.isArray(item.zones) && 
                        item.zones.some(zone => zone.id && zone.id.id === zoneId && !ignoreIds.includes(zone.id.id))
                    );
        } else {
            return widgetContext.$scope.datasource.some(item => 
                        item.eventType === eventType &&
                        Array.isArray(item.zones) && 
                        item.zones.some(zone => zone.id && zone.id.id === zoneId)
                    );
        }

    }

    function checkGeofenceOccupied(eventType, geofenceId, ignoreIds=[]){
        if(ignoreIds.length){
            ignoreIds = ignoreIds.map(x=>x.id.id)
        }

        if(eventType == vm.type){
            return widgetContext.$scope.datasource.some(item => 
                        item.eventType === eventType &&
                        Array.isArray(item.geofences) && 
                        item.geofences.some(geofence => geofence.id && geofence.id.id === geofenceId && !ignoreIds.includes(geofence.id.id))
                    );
        } else {
            return widgetContext.$scope.datasource.some(item => 
                        item.eventType === eventType &&
                        Array.isArray(item.geofences) && 
                        item.geofences.some(geofence => geofence.id && geofence.id.id === geofenceId)
                    );
        }

    }

    function getRuleInfo(){
        vm.form.patchValue({
            index: additionalParams.index,
            eventType: additionalParams.eventType,
            duration: additionalParams.duration
        })
        vm.select("getZones", {value: additionalParams.locations}, true);
    }

    vm.save = function() {
        let values = vm.form.value;

        const index = widgetContext.$scope.datasource.findIndex(x => x.index === values.index);

        if (index != -1) {
            widgetContext.$scope.datasource[index] = values;
        }

        widgetContext.$scope.alarmConfig.locationThresholds = widgetContext.$scope.datasource; 

        attributeService.saveEntityAttributes(params.selectedEntity.entityId, "SERVER_SCOPE", [{key: "alarmConfig", value: widgetContext.$scope.alarmConfig}]).subscribe(()=>{
            widgetContext.updateAliases();
            vm.dialogRef.close(null);
        });
    };
}

