function openDeleteEntityDialog() {
    let title = 'Delete alarm rule';
    let content = 'Are you sure you want to delete the alarm rule?';
    dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
        function(result) {
            if (result) {
                const updatedArray = deleteElementByIndex(widgetContext.$scope.datasource, additionalParams.index)
                widgetContext.$scope.alarmConfig.locationThresholds = updatedArray;

                attributeService.saveEntityAttributes(params.selectedEntity.entityId, "SERVER_SCOPE", [{key: "alarmConfig", value: widgetContext.$scope.alarmConfig}]).subscribe(()=>{
                    widgetContext.updateAliases();
                });
            }
        }
    );
}

function deleteElementByIndex(dataArray, index) {
    const elementIndex = dataArray.findIndex(item => item.index === index);

    if (elementIndex !== -1) {
        dataArray.splice(elementIndex, 1);
    }

    return dataArray;
}

