let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'));

openDeleteEntityDialog();

function openDeleteEntityDialog() {
    let title = `Are you sure you want to delete location?`;
    let content = `Note: Deleting a location will delete the Alert Rules if they were created under this location.`;
    dialogs.confirm(title, content, 'Cancel', 'Delete')
        .pipe(
            widgetContext.rxjs.switchMap((data) => {
                if (data) {
                    return deleteLocation(); 
                } else {
                    return widgetContext.rxjs.of(null);
                }
            })
        )
        .subscribe();
}

function deleteLocation() {
    entityRelationService.findByToAndType(entityId, "ZoneToLocation")
        .pipe(
            widgetContext.rxjs.switchMap(zones => {
            if (!zones || zones.length === 0) {
                return widgetContext.rxjs.of([]);
            }

            const zoneIds = zones.map(zone => zone.from.id);

            const geofenceObservables = zones.map(zone => 
                entityRelationService.findByToAndType(zone.from, "GeofenceToZone").pipe(widgetContext.rxjs.defaultIfEmpty([]))
            );

            return widgetContext.rxjs.forkJoin(geofenceObservables).pipe(
                widgetContext.rxjs.map(geofencesArray => {
                    const geofenceIds = geofencesArray.flatMap(geofences =>
                        geofences.map(geofence => geofence.from.id)
                    );

                    return [...zoneIds, ...geofenceIds];
                })
            );
        }),
        widgetContext.rxjs.defaultIfEmpty([])
        ).subscribe(idsToDelete => {
            idsToDelete.push(entityId.id);

            widgetContext.rxjs.from(idsToDelete).pipe(
                widgetContext.rxjs.concatMap(id => assetService.deleteAsset(id))
            ).subscribe({
                complete: () => {
                    widgetContext.showSuccessToast(`"${entityName}" location was successfully deleteted!`, 4000, "bottom", "left", "layout");
                    widgetContext.updateAliases();
                }
            });

        });
}

