actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetProfileService = $injector.get(widgetContext.servicesMap.get("assetProfileService"));
let attributeService = $injector.get(widgetContext.servicesMap.get("attributeService"));
let entityRelationService = $injector.get(widgetContext.servicesMap.get("entityRelationService"));
let assetService = $injector.get(widgetContext.servicesMap.get("assetService"));
let broadcastService = $injector.get(widgetContext.servicesMap.get("broadcastService"));
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
const controller = widgetContext.$scope.ctx.stateController;
const params = controller.getStateParams();

openZoneDialog();

function openZoneDialog() {
    customDialog.customDialog(htmlTemplate, ZoneCreateDialogController).subscribe();
}

function ZoneCreateDialogController(instance) {
    let vm = instance;

    vm.isImageFilled = additionalParams.entity.image != null;
    vm.entityId = entityId;
    vm.widgetContext = widgetContext;

    const {longitude, latitude} = params.selectedEntity;
    const defaultColor = "#0086C9";

    vm.zoneInfoFormGroup = vm.fb.group({
        name: [additionalParams.entity.Name, [vm.validators.required, vm.validators.maxLength(40)]],
        description: [additionalParams.entity.Description, []]
    });

    vm.zoneTypeFormGroup = vm.fb.group({
        type: ["Shape zone", []],
        image: [null, []],
        showLayerMap: [false, []],
        color: [additionalParams.entity.color ? additionalParams.entity.color: defaultColor, []]
    });

    const zoneTypes = ["Image plan", "Image on map", "Shape"];

    const zoneTypeDataMap = {
        "Shape": {
            type: "Shape zone",
            image: additionalParams.entity.image,
            showLayerMap: false
        },
        "Image plan": {
            type: "Image",
            image: additionalParams.entity.image,
            showLayerMap: false
        },
        "Image on map": {
            type: "Image",
            image: additionalParams.entity.image,
            showLayerMap: true
        }
    };

    widgetContext.detectChanges();

    vm.updateGeofences = function() {
        vm.thirdStepInitialized = true;
        broadcastService.broadcast('geofencesUpdate', 'Some data');
    };

    broadcastService.on('coordinatesChanged', (event, args) => {
        const imageControl = vm.zoneTypeFormGroup.get('image');

        if(imageControl) {
            imageControl.setErrors({ customError: 'Click save firstly.' });
        }
    });

    broadcastService.on('coordinatesSaved', (event, args) => {
        const imageControl = vm.zoneTypeFormGroup.get('image');

        if(imageControl) {
            imageControl.setErrors(null)
        }
    });

    vm.mapStateId = "shapeZoneMap";
    vm.geofenceMapStateId = "shapeZoneGeofencesMap";
    vm.zoneType = "Image on map";

    vm.mapStateIds = ["shapeZoneMap", "layerImageZoneMap", "staticImageZoneMap"];
    vm.geofenceMapStateIds = ["shapeZoneGeofencesMap", "layerImageZoneGeofencesMap", "staticImageZoneGeofencesMap"];
    vm.zoneTypeIdMap = {};

    vm.zoneTypeFormGroup.get("color").valueChanges
        .pipe(
            widgetContext.rxjs.switchMap((color) => {
                const colorAttr = [{
                    key: "color",
                    value: color
                }];

                return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", colorAttr);
            })
        )
        .subscribe();

    vm.zoneTypeFormGroup.get("type").valueChanges.subscribe(value => {
        clearGeofences();

        const imageControl = vm.zoneTypeFormGroup.get('image');

        if(imageControl) {
            if(value == "Image"){
                imageControl.setValidators([vm.validators.required]);
                imageControl.updateValueAndValidity();
                imageControl.setErrors({ customError: 'Click save firstly.' });
            } else {
                imageControl.clearValidators();
                imageControl.updateValueAndValidity();
            }
        }
    });


    vm.zoneTypeFormGroup.get("showLayerMap").valueChanges.subscribe(value => {
        clearGeofences();

        if(value){
            const imageControl = vm.zoneTypeFormGroup.get('image');

            if(imageControl){
                imageControl.setErrors({ customError: 'Click save firstly.' });
            }
        }
    });

    function clearGeofences(){
        const geofences = vm.zoneGeofencesFormGroup ? vm.zoneGeofencesFormGroup.value.geofences : null;

        if(geofences && geofences.length){
            geofences.forEach(()=>vm.removeGeofence(0, false));
        }
    }

    vm.zoneTypeFormGroup.valueChanges.subscribe(value => {
        if (value.type === "Shape zone") {
            vm.mapStateId = "shapeZoneMap";
            vm.geofenceMapStateId = "shapeZoneGeofencesMap";
            vm.zoneType = "Shape";
        } else {
            if (!value.image) {
                vm.mapStateId = "selectImageState";
            } else {
                if (value.showLayerMap) {
                    vm.mapStateId = "layerImageZoneMap";
                    vm.geofenceMapStateId = "layerImageZoneGeofencesMap";
                    vm.zoneType = "Image on map";
                } else {
                    vm.mapStateId = "staticImageZoneMap";
                    vm.geofenceMapStateId = "staticImageZoneGeofencesMap";
                    vm.zoneType = "Image plan";
                }
            }
        }

        widgetContext.detectChanges();
    });

    vm.zoneTypeFormGroup.patchValue(zoneTypeDataMap[additionalParams.entity.Type]);

    vm.temporaryGeofences = [];

    vm.zoneTypeFormGroup.get("image").valueChanges
        .pipe(
            widgetContext.rxjs.switchMap((value) => {
                const perimeter = `[[${latitude+0.001}, ${longitude+0.001}], [${latitude+0.001}, ${longitude-0.001}], [${latitude-0.001}, ${longitude-0.001}], [${latitude-0.001}, ${longitude+0.001}]]`;

                const zoneAssetImageAttributes = [
                    {
                        key: "image",
                        value
                    },
                    {
                        key: "perimeter",
                        value: perimeter
                    },
                ];

                return attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", zoneAssetImageAttributes);
            })
        )
        .subscribe();

    vm.zoneGeofencesFormGroup = vm.fb.group({
        geofences: vm.fb.array([])
    });

    vm.initGeofences = () => {
        const pageLink = widgetContext.pageLink(100);

        const queryParams = {
            entityFilter: {
                type: "assetSearchQuery",
                rootEntity: entityId,
                direction: "TO",
                maxLevel: 1,
                fetchLastLevelOnly: false,
                relationType: "GeofenceToZone",
                assetTypes: [
                    "Geofence"
                ]
            },
            entityFields: [{
                type: "ENTITY_FIELD",
                key: "name"
            }],
            latestValues: [{
                type: "ATTRIBUTE",
                key: "color"
            }],
            keyFilters: [],
            pageLink
        };

        widgetContext.http.post(`/api/entitiesQuery/find`, queryParams)
            .subscribe(response => {
                response.data.forEach((element) => {
                    const name = element.latest.ENTITY_FIELD.name.value;
                    const entityId = element.entityId;

                    const color = element.latest.ATTRIBUTE.color.value;

                    vm.addGeofence(name, true, entityId, color);
                });
            });
    };

    vm.resetImage = function(){
        vm.mapStateId = "selectImageState";

        vm.zoneTypeFormGroup.patchValue({
            image: null
        });

        vm.isImageFilled = false;
        widgetContext.detectChanges();
    };

    vm.initGeofences();

    vm.addGeofence = (name, active, id, color) => {
        const geofenceForm = vm.fb.group({
            name: [name, []],
            active: [active, []],
            id: [id, []],
            color: [color, []]
        });

        vm.zoneGeofencesFormGroup.get("geofences").push(geofenceForm);
    };

    vm.removeGeofence = (index, confirmDialog) => {
        if(confirmDialog){
            let title = 'Are you sure you want to delete geofence?';
            let content = 'Note: Deleting a geofence will delete the Alert Rules if they were created under this geofence.';
            dialogs.confirm(title, content, 'Cancel', 'Delete').subscribe(
                function(result) {
                    if (result) {
                        geofenceDeletion(index);
                    }
                }
            );
        } else {
            geofenceDeletion(index);
        }
    };

    function geofenceDeletion(index){
        const {id} = vm.zoneGeofencesFormGroup.get("geofences").at(index).value;

        if (id) {
            assetService.deleteAsset(id.id).subscribe(() => {
                for (let i = 0; i <= vm.temporaryGeofences.length - 1; i++) {
                    if (vm.temporaryGeofences[i] === id.id) {
                        vm.temporaryGeofences.splice(i, 1);
                    }
                }
                vm.updateGeofences();

                widgetContext.detectChanges();
            });
        }

        vm.zoneGeofencesFormGroup.get("geofences").removeAt(index);
    }

    vm.activateGeofence = (index) => {
        const {name, id, color} = vm.zoneGeofencesFormGroup.get("geofences").at(index).value;
        const geofenceAssetPayload = {
            name,
            id,
            assetProfileId: geofenceProfileId
        };

        const colorAttribute = [{
            key: "color",
            value: color
        }];

        assetService.saveAsset(geofenceAssetPayload).
            pipe(
                widgetContext.rxjs.switchMap((response) => {
                    const responseId = response.id;

                    const requests = [
                        attributeService.saveEntityAttributes(responseId, "SERVER_SCOPE", colorAttribute)
                    ];

                    const geofenceFormGroup = vm.zoneGeofencesFormGroup.get("geofences").at(index);

                    if(!vm.temporaryGeofences.includes(responseId.id)) {
                        vm.temporaryGeofences.push(responseId.id);
                    }
                    geofenceFormGroup.get("id").setValue(responseId);
                    geofenceFormGroup.get("name").disable();
                    geofenceFormGroup.get("active").setValue(true);
                    geofenceFormGroup.markAsPristine();
                    widgetContext.detectChanges();

                    if (!id) {
                        const relation = {
                            from: responseId,
                            to: entityId,
                            type: "GeofenceToZone",
                            typeGroup: "COMMON"
                        };

                        requests.push(entityRelationService.saveRelation(relation));
                    }

                    return widgetContext.rxjs.forkJoin(requests);
                })
            )
            .subscribe(() => {
                vm.updateGeofences();

                widgetContext.detectChanges();
            });
    };

    vm.editGeofence = (index) => {
        vm.zoneGeofencesFormGroup.get("geofences").at(index).get("name").enable();
        vm.zoneGeofencesFormGroup.get("geofences").at(index).get("active").setValue(false);
        vm.zoneGeofencesFormGroup.get("geofences").at(index).markAsPristine();
        vm.updateGeofences();
        widgetContext.detectChanges();
    };

    vm.chooseTypeOptions = ["Image", "Shape zone"];

    let geofenceProfileId;

    const pageLink = widgetContext.pageLink(100);

    const assetTypes = ["Image plan", "Image on map", "Shape"];

    const assetTypeOptions = [];
    assetProfileService.getAssetProfiles(pageLink).subscribe(response => {
        response.data.forEach((assetProfile) => {
            if (assetTypes.includes(assetProfile.name)) {
                assetTypeOptions.push({
                    id: assetProfile.id,
                    name: assetProfile.name
                });
            }

            if(assetProfile.name === "Geofence") {
                geofenceProfileId = assetProfile.id;
            }

            if (zoneTypes.includes(assetProfile.name)) {
                vm.zoneTypeIdMap[assetProfile.name] = assetProfile.id;
            }
        });

        widgetContext.$scope.assetTypeOptions = vm.assetTypeOptions;
        widgetContext.detectChanges();
    });


    const deleteTemporaryGeofences = function() {
        const idsToDelete = [...vm.temporaryGeofences];

        return widgetContext.rxjs.forkJoin(
            idsToDelete.map((id) => assetService.deleteAsset(id))
        );
    };

    vm.cancel = function() {
        vm.dialogRef.close(null);

        if (vm.temporaryGeofences && vm.temporaryGeofences) {
            deleteTemporaryGeofences()
        }
    };

    vm.onStepChange = function(event){
        if(event.selectedIndex == 2){
            vm.updateGeofences();
        }
    }

    vm.save = function() {
        const {description, name} = vm.zoneInfoFormGroup.value;

        const assetPayload = {
            additionalInfo: {
                description
            },
            name,
            id: vm.entityId,
            assetProfileId: vm.zoneTypeIdMap[vm.zoneType]
        };

        const locationId = params.selectedEntity.entityId;

        assetService.saveAsset(assetPayload)
            .pipe(
                widgetContext.rxjs.switchMap((response) => {
                    const relation = {
                        from: entityId,
                        to: locationId,
                        type: "ZoneToLocation",
                        typeGroup: "COMMON"
                    };

                    const descriptionAttribute = [{
                        key: "description",
                        value: description
                    }];

                    return widgetContext.rxjs.forkJoin([
                        entityRelationService.saveRelation(relation),
                        attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", descriptionAttribute)
                    ]);
                })
            )
            .subscribe(() => {
                widgetContext.updateAliases();
                widgetContext.showSuccessToast(`${name} zone was created successfully!`, 4000, "bottom", "left", "layout");
                vm.dialogRef.close(null);
            });
    };
}

