actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let assetService = $injector.get(widgetContext.servicesMap.get('assetService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'))
let entityRelationService = $injector.get(widgetContext.servicesMap.get('entityRelationService'))

let http = $injector.get(widgetContext.servicesMap.get('http'))
let params = widgetContext.stateController.getStateParams();

openDeleteAssetWindow();

function openDeleteAssetWindow() {
    customDialog.customDialog(htmlTemplate, DeleteAssetDialogController).subscribe();
}

function DeleteAssetDialogController(instance) {
    let vm = instance;

    let locationId = params.selectedEntity.entityId;

    vm.entityName = entityName;

    vm.cancel = function() {
        vm.dialogRef.close(null);
    };

    vm.delete = function() {
        const assetsToDelete = [entityId.id];
        const pageLink = widgetContext.pageLink(100);

        const queryParams = {
            entityFilter: {
                type: "assetSearchQuery",
                rootEntity: entityId,
                direction: "TO",
                maxLevel: 1,
                fetchLastLevelOnly: false,
                relationType: "GeofenceToZone",
                assetTypes: [
                    "Geofence"
                ]
            },
            entityFields: [
                {
                    type: "ENTITY_FIELD",
                    key: "name"
                }
            ],
            pageLink
        };

        widgetContext.http.post(`/api/entitiesQuery/find`, queryParams)
            .pipe(
                widgetContext.rxjs.switchMap((response) => {
                    response.data.forEach((element) => {
                        const geofenceId = element.entityId.id;

                        assetsToDelete.push(geofenceId);
                    });

                    let requests = assetsToDelete.map(id => assetService.deleteAsset(id));

                    if(locationId){
                        requests.unshift(entityRelationService.deleteRelation(entityId, "ZoneToLocation", locationId));
                    }

                    return widgetContext.rxjs.forkJoin(requests);
                })
            ).subscribe(() => {
                widgetContext.showSuccessToast(`"${entityName}" asset was successfully deleteted!`, 4000, "bottom", "left", "layout");
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
            });
    };
}

