settings.cellContentFunction:
if(value && value=='true'){
    return `<div style="border-radius: 4px;
    height: fit-content;
    font-size: 12px;
    font-weight: 600;
    padding: 4px 8px;
    width: fit-content;
    background-color: #E0F2FE;
    color: #026AA2;">
        ACTIVE</div>`;  
} else {
    return `<div style="border-radius: 4px;
    height: fit-content;
    font-size: 12px;
    font-weight: 600;
    padding: 4px 8px;
    width: fit-content;
    background-color: #EAECF0;
    color: #475467;">
        INACTIVE</div>`;  
}

if (entity.latitude && entity.longitude) {
    return entity.longitude + ", " + entity.latitude;
}
return value;

if(typeof value == "number"){
    if(value > 100 || value < 0) {
        return "No info";
    } else {
        value = Number(value.toFixed(0));
    }

    let batteryElement;

    if (value >= 60) {
        batteryElement = `
            <div style="width: 30px;height: 16px;border: 2px solid #067647;border-radius: 6px;background-color: #DCFAE6; margin-right: 8px">
                <div style="padding: 2px;width: 100%;height:100%">
                    <div style="background-color: #067647; width: ${value}%; height: 100%;border-radius: 2px;"></div>
                </div>
            </div>`; 
    } else if (value < 60 && value > 20) {
        batteryElement = `
            <div style="width: 30px;height: 16px;border: 2px solid #B54708;border-radius: 6px;background-color: #FEF0C7; margin-right: 8px">
                <div style="padding: 2px;width: 100%;height:100%">
                    <div style="background-color: #B54708; width: ${value}%; height: 100%;border-radius: 2px;"></div>
                </div>
            </div>`;
    } else {
        batteryElement = `
            <div style="width: 30px;height: 16px;border: 2px solid #B42318;border-radius: 6px;background-color: #FEE4E2; margin-right: 8px">
                <div style="padding: 2px;width: 100%;height:100%">
                    <div style="background-color: #B42318; width: ${value}%; height: 100%;border-radius: 2px;"></div>
                </div>
            </div>`;
    }

    return `
        <div style="display: flex; align-items: center">
            ${batteryElement}
            <div style="font-size: 14px; font-weight: 600; line-height: 20px; color: #0C111D">${value}</div>
        </div>
    `;
} else {
    return "No info";
}

