actionCellButton.customFunction:
let $injector = widgetContext.$scope.$injector;
let customDialog = $injector.get(widgetContext.servicesMap.get('customDialog'));
let attributeService = $injector.get(widgetContext.servicesMap.get("attributeService"));
let deviceService = $injector.get(widgetContext.servicesMap.get("deviceService"));
let assetService = $injector.get(widgetContext.servicesMap.get("assetService"));
let entityService = $injector.get(widgetContext.servicesMap.get("entityService"));
let entityRelationService = $injector.get(widgetContext.servicesMap.get("entityRelationService"));
let broadcastService = $injector.get(widgetContext.servicesMap.get("broadcastService"));
let controller = widgetContext.stateController;
let params = controller.getStateParams();

openEntityDialog();

function openEntityDialog() {
    customDialog.customDialog(htmlTemplate, EntityEditDialogController).subscribe();
}

function EntityEditDialogController(instance) {
    let vm = instance;

    vm.connectivityOptions = [
        "RFID (MQTT)",  "LoRa", "WiFi", "Bluetooth", "UWD"];
    vm.configPresetOptions = [];
    vm.locationOptions = [];
    vm.zoneOptions = [];
    vm.stateId = null;
    vm.isInit = false;
    vm.widgetContext = widgetContext;
    vm.stateIds = ["deviceImageMap", "deviceImageOnMap", "deviceShapeMap"];
    vm.entityId = entityId;
    vm.isRelationChanged = false;
    vm.initialZone = additionalParams.entity.zone;
    vm.deviceType = additionalParams.entity.Type;

    const pageLink = widgetContext.pageLink(1073741824);

    vm.entityInfoFormGroup = vm.fb.group({
        name: [additionalParams.entity.Name, [vm.validators.required, vm.validators.maxLength(40)]],
        description: [additionalParams.entity.description],
        modelNumber: [additionalParams.entity.modelNumber],
        serialNumber: [additionalParams.entity["Serial Number"]]
    });

    assetService.getTenantAssets(pageLink, 'Location').subscribe(locations => {
        if(locations && locations.data){
            locations.data.forEach(x=>{
                vm.locationOptions.push({
                    name: x.name,
                    id: x.id
                });
            });
        }
    });

    vm.settingsFormGroup = vm.fb.group({ 
        type: [vm.deviceType, [vm.validators.required]],
        configPreset: [additionalParams.entity['Config preset'], []],
        connectivity: [additionalParams.entity.Connectivity, []],
        location: [additionalParams.entity.location, []],
        zone: [vm.initialZone, []]
    }); 

    vm.imageFormGroup = vm.fb.group({
        image: [additionalParams.entity.image, []]
    });

    vm.selectLocation = function(event, auto){
        entityService.findEntityDataByQuery({
            entityFilter: {
                "type": "relationsQuery",
                "resolveMultiple": true,
                "rootStateEntity": false,
                "stateEntityParamName": null,
                "defaultStateEntity": null,
                "rootEntity": event.value.id,
                "direction": "TO",
                "maxLevel": 1,
                "filters": [
                    {
                      "relationType": "ZoneToLocation",
                      "entityTypes": [
                        "ASSET"
                      ]
                    }
                ],
                "fetchLastLevelOnly": false,
            },
            entityFields: [{
              "type": "ENTITY_FIELD",
              "key": "name"
            }, {
              "type": "ENTITY_FIELD",
              "key": "type"
            }],
            pageLink
        }).subscribe(zones=>{
            vm.zoneOptions = [];
            if(zones && zones.data && zones.data.length){
                zones.data.forEach(zone=>{
                    vm.zoneOptions.push({
                        name: zone.latest.ENTITY_FIELD.name.value,
                        id: zone.entityId,
                        type: zone.latest.ENTITY_FIELD.type.value
                    });
                });
            }
            if(auto && vm.deviceType != "Tag"){
                vm.settingsFormGroup.get("zone").setValue(vm.initialZone);
                if (vm.initialZone && typeof vm.initialZone === "string"){
                    vm.selectZone({value: JSON.parse(vm.initialZone)}, auto);
                }
            }
        });
    };

    if(vm.deviceType != "Tag"){
        vm.selectLocation({value: JSON.parse(additionalParams.entity.location)}, true);
    }

    vm.settingsFormGroup.get('location').valueChanges.subscribe((value) => {
        entityRelationService.findByFromAndType(vm.entityId, "AnchorToZone").pipe(
            widgetContext.rxjs.switchMap((response) => {
                if(response.length > 0){
                    const deleteRequests = response.map(responseItem => (
                        entityRelationService.deleteRelation(responseItem.from, responseItem.type, responseItem.to)
                    ));

                    return widgetContext.rxjs.forkJoin(deleteRequests);
                }
            })
        ).subscribe(()=>{
            vm.settingsFormGroup.controls["zone"].setErrors({required: true});
            vm.isInit = false;
        })
    });

    vm.compareFn = function(c1, c2) {
        if(typeof(c2) == "string"){
            return c1 && c2 ? c1.id.id === JSON.parse(c2).id.id : c1 === c2;
        } else return c1 && c2 ? c1.id.id === c2.id.id : c1 === c2;
    }

    function changeDeviceZone(relationPayload, zoneToSave, isAuto) {
        return entityRelationService.findByFromAndType(entityId, "AnchorToZone")
            .pipe(
                widgetContext.rxjs.switchMap((response) => {
                    const requests = [
                        entityRelationService.saveRelation(relationPayload),
                        attributeService.saveEntityAttributes(entityId, "SERVER_SCOPE", [{key: "zone", value:zoneToSave}])
                    ];

                    if(response.length > 0){
                        if(!isAuto){
                            vm.initialRelation = response[0];
                            vm.isRelationChanged = true;
                        }

                        const deleteRequests = response.map(responseItem => (
                            entityRelationService.deleteRelation(responseItem.from,responseItem.type,responseItem.to)
                        ));

                        return widgetContext.rxjs.forkJoin(deleteRequests)
                            .pipe(
                                widgetContext.rxjs.switchMap(() => (
                                    widgetContext.rxjs.forkJoin(requests)
                                ))
                            )
                    }

                    return widgetContext.rxjs.forkJoin(requests)
                })
            )
    }

    vm.selectZone = function(event, auto){
        let zone = event.value;

        if(zone.type == "Shape"){
            vm.stateId = "deviceShapeMap";
        } else if(zone.type == "Image plan"){
            vm.stateId = "deviceImageMap";
        } else {
            vm.stateId = "deviceImageOnMap";
        }

        const saveRelationPayload = {
            from: vm.entityId,
            to: zone.id,
            type: 'AnchorToZone',
            typeGroup: 'COMMON'
        };

        changeDeviceZone(saveRelationPayload, zone, auto).subscribe(()=>{
            broadcastService.broadcast('geofencesUpdate', 'Some data');
            vm.isInit = true;
        });
    };

    assetService.getTenantAssets(pageLink, 'deviceConfig').subscribe(res=>{
        vm.configPresetOptions = [];
        if(res && res.data && res.data.length){
            res.data.forEach(deviceConfig=>{
                vm.configPresetOptions.push(deviceConfig.name);
            });
        }
    });

    vm.deviceTypeOptions = [];

    const queryParams = {
        entityFilter: {
            type: "entityType",
            entityType: "DEVICE_PROFILE",
        },
        entityFields: [
            {
                type: "ENTITY_FIELD",
                key: "name"
            }
        ],
        keyFilters: [
            {
                key: {
                    type: "ENTITY_FIELD",
                    key: "name"
                },
                predicate: {
                    type: "COMPLEX",
                    operation: "OR",
                    predicates: [
                        {
                            operation: "EQUAL",
                            value: {
                                defaultValue: "Tag",
                            },
                            type: "STRING"
                        },
                        {
                            operation: "EQUAL",
                            value: {
                                defaultValue: "Anchor",
                            },
                            type: "STRING"
                        }
                    ]
                }
            }
        ],
        pageLink
    };

    widgetContext.http.post(`/api/entitiesQuery/find`, queryParams)
        .subscribe(response => {
            response.data.forEach(deviceType => {
                const option = {
                    id: deviceType.entityId,
                    name: deviceType.latest.ENTITY_FIELD.name.value
                };

                vm.deviceTypeOptions.push(option);

                if (option.name === vm.deviceType) {
                    vm.settingsFormGroup.get("type").setValue(option);
                }
            });

            widgetContext.detectChanges();
        });

    vm.cancel = function() {
        if(vm.isRelationChanged){
            returnOldRelations();
        } else vm.dialogRef.close(null);
    };

    window.addEventListener('beforeunload', function (event) {
        if (vm.isRelationChanged) {
            const confirmationMessage = 'Are you sure you want to leave this page? Your changes may not be saved.';

            event.returnValue = confirmationMessage;

            return confirmationMessage;
        }
    });

    window.addEventListener('unload', function () {
        if (event.returnValue) {
            returnOldRelations();
        }
    });

    function returnOldRelations(){
        if(vm.isRelationChanged){
            changeDeviceZone(vm.initialRelation, vm.initialZone, false).subscribe(()=>{
                vm.isRelationChanged = false;
                vm.dialogRef.close(null);
            })
        }
    };

    vm.save = function() {
        const entityInfo = vm.entityInfoFormGroup.value;
        const entitySettings = vm.settingsFormGroup.value;
        const zoneId = entitySettings.zone.id;

        vm.entity = {
            deviceProfileId: entitySettings.type.id,
            id: entityId,
            additionalInfo: {
                description: entityInfo.description
            },
            name: entityInfo.name
        };

        deviceService.saveDevice(vm.entity)
            .pipe(
                widgetContext.rxjs.switchMap((response) => {
                    let attributes = [
                        {
                            key: "image",
                            value: vm.imageFormGroup.value.image
                        },
                        {
                            key: "serialNumber",
                            value: entityInfo.serialNumber
                        },
                        {
                            key: "modelNumber",
                            value: entityInfo.modelNumber
                        },
                        {
                            key: "description",
                            value: entityInfo.description
                        },
                        {
                            key: "connectivity",
                            value: entitySettings.connectivity
                        }
                    ];

                    if(entitySettings.type.name == "Anchor"){
                        attributes.push({
                            key: "location",
                            value: entitySettings.location
                        },{
                            key: "zone",
                            value: entitySettings.zone
                        })
                    } else {
                        attributes.push({
                            key: "configPreset",
                            value: entitySettings.configPreset
                        });
                    }

                    const requests = [attributeService.saveEntityAttributes(response.id, "SERVER_SCOPE", attributes)];

                    return widgetContext.rxjs.forkJoin(requests);
                })
            )
            .subscribe(() => {
                widgetContext.updateAliases();
                vm.dialogRef.close(null);
                widgetContext.showSuccessToast(`${entityInfo.name} device was created successfully!`, 4000, "bottom", "left", "layout");
            });
    };
}

let $injector = widgetContext.$scope.$injector;
let dialogs = $injector.get(widgetContext.servicesMap.get('dialogs'));
let deviceService = $injector.get(widgetContext.servicesMap.get('deviceService'));
let attributeService = $injector.get(widgetContext.servicesMap.get('attributeService'));

openDeleteEntityDialog();

function openDeleteEntityDialog() {
    let title = `Delete device`;
    let content = `Are you sure you want to delete the ${entityName} device?`;
    dialogs.confirm(title, content, 'Cancel', 'Delete')
        .pipe(
            widgetContext.rxjs.switchMap((data) => {
                if (data) {
                    return deleteEventTelemetryValue(); 
                } else {
                    return widgetContext.rxjs.of(null);
                }
            })
        )
        .subscribe();
}

function deleteEventTelemetryValue() {

    const requests = [
        deviceService.deleteDevice(entityId.id)
    ];

    try {
        if(additionalParams.entity["Assigned asset"]){
            console.log(additionalParams.entity["Assigned asset"])
            const preAssignedAsset = JSON.parse(additionalParams.entity["Assigned asset"]);
            const assetAttributes = [
                {
                    key: "hasRelatedDevice",
                    value: false
                },
                {
                    key: "assignedDevice",
                    value: null
                }
            ];

            requests.push(attributeService.saveEntityAttributes(preAssignedAsset.id, "SERVER_SCOPE", assetAttributes));
        }
    } catch(err) {}

    return widgetContext.rxjs.forkJoin(requests)
        .pipe(
            widgetContext.rxjs.tap(() => {
                widgetContext.showSuccessToast(`${entityName} device was deleted successfully!`, 4000, "bottom", "left", "layout");
                widgetContext.updateAliases();
            }
        ));
}

