settings.markerImageFunction:
var type = data['Type'];
var isAlarm = data['alertActivity'];
var isActive = data['status'];

let index;

if (type == 'Tool'){
    index = 0;
} else if(type == "Box"){
    index = 3;
} else if(type == "Worker"){
    index = 6;
} else if(type == "Loader"){
    index = 9;
}
if(isAlarm == "true"){
    index += 1;
} else {
    if(isActive != "true"){
        index += 2;
    }
}

return {
    url: images[index],
    size: 28
}

