        {
            stateId: 'assetDetails', 
            name: entityLabel||entityName
        }
    ],
    selectedEntity: {
        entityName,
        entityId,
        entityLabel
    }
};

history.pushState({}, '');

widgetContext.updateAliases();
controller.updateState('default', newParams);

