if(typeof value == "number"){
    if(value > 100 || value < 0) {
        return "No info";
    } else {
        return Number(value.toFixed(0));
    }
} else {
    return "No info";
}

settings.markdownTextFunction:
let type = data[0].Type;
let currentState = "locationAlertCard"

if(type == "Location"){
    currentState = "locationAlertCard";
} else {
    currentState = "zoneAlertCard";
}

return `
    <div *ngIf="ctx && ctx.stateController" class="stateBlock">
        <tb-dashboard-state class="mainState" [ctx]="ctx" [syncParentStateParams]="true" stateId="${currentState}">
        </tb-dashboard-state>
    </div>
`;

