configuration.tbelScript:
var alarmSettings = JSON.parse(metadata.alarmSettings).profileSettings;
var alarmMsgs = [];

foreach(setting: alarmSettings.variables.entrySet()) {
    var keyName = setting.getKey();
    var alarmRules = setting.getValue();

    if (msg[keyName] == null || alarmRules == null ||
        alarmRules.isEmpty()) {
        break;
    }

    var alarmVariable = msg[keyName];

    foreach(alarmRule: alarmRules) {
        if (alarmRule.enabled == null || !alarmRule.enabled) {
            alarmMsgs.push(buildReturnMsg(alarmRule,false));
            break;
        }

        var create = overThreshold(alarmRule);
        alarmMsgs.push(buildReturnMsg(alarmRule,create));
    }


}
msg.alarmSettings = alarmSettings;

if (alarmMsgs.isEmpty()) {
    return {msg: msg, metadata: metadata, msgType: msgType};
}
return alarmMsgs;


function buildReturnMsg(alarmRule,create) {
        var notificationSettings = JSON.stringify(alarmRule.notifications);

    return {
        msg: alarmRule.alarmFields,
        metadata: {
            ts: metadata.ts,
            create: create,
            notificationSettings: notificationSettings
        },
        msgType: "ALARM"
    }
}

function overThreshold(alarmRule){
    var isLower = alarmRule.lowThresholdValue != null ? alarmVariable < alarmRule.lowThresholdValue : null;
    var isHigher = alarmRule.highThresholdValue != null ? alarmVariable > alarmRule.highThresholdValue : null;
    return isLower === true || isHigher === true;
}

