configuration.tbelScript:
var alarmSettings = JSON.parse(metadata.alarmSettings).profileSettings;
if(profileSettings.isEmpty()){
    return {msg: msg, metadata: metadata, msgType: msgType};
}

var alarmRules = alarmSettings.perimeters[metadata.area];

if (ruleIsEmpty(alarmRules)) {
    return {msg: msg, metadata: metadata, msgType: msgType};
}


var areaAlarmRule = alarmRules[metadata.areaId][metadata.status];
return buildReturnMsg(areaAlarmRule);


function ruleIsEmpty(alarmRules) {
    return alarmRules == null ||
        alarmRules[metadata.areaId] == null ||
        alarmRules[metadata.areaId][metadata.status]==null;
}

function buildReturnMsg(alarmRule) {
        var notificationSettings = JSON.stringify(alarmRule.notifications);

    return {
        msg: alarmRule.alarmFields,
        metadata: {
            ts: metadata.ts,
            create: alarmRule.enabled,
            notificationSettings: notificationSettings
        },
        msgType: "ALARM"
    }
}

