descriptor.templateCss:
#navContainer {
    overflow: auto;
    width: 100%;
    height: 100%;
    display: flex;
    padding: 50px 8px;
    flex-direction: column;
    align-items: baseline;
    justify-content: space-between;
    color: #495057;
}

#navContainer .navBlock{
    display: flex;
    flex-direction: column;
    align-content: center;
    width: 95%;
    flex: 1;
    padding-top: 15px;
}

#navContainer .navBlock .firstBlock{
    width: 100%;   
}

.hideMenu{
    display: none;
}

#navContainer .navBlock .navItem{
    display: flex;
    align-items: center;
    color: #384250;
    padding: 7px 10px;
    margin: 3px;
    cursor: default;
    font-size: 14px;
}

#navContainer .navBlock .navItem span{
    width: fit-content;
    line-height: 20px;
}

#navContainer .navBlock .navItem.dark{
    color: #D4D4D6;
}

#navContainer .navBlock .navItem mat-icon{
    font-size: 24px;
    margin-right: 8px;
}

#navContainer .navBlock .navItem:hover{
    background: var(--tb-primary-50);
    border-radius: 5px;
    color: var(--tb-primary-500);
}

#navContainer .navBlock .navItem.dark:hover{
    background: #163b74;
    color: #5E9CFF;
}

#navContainer .navBlock .menuItem{
    cursor: pointer;
}

#navContainer .navBlock .navItem svg{
    margin-right: 8px;
}

#navContainer .navBlock .navItem.active{
    background: var(--tb-primary-100);
    color: var(--tb-primary-500);
    font-weight: 500;
    border-radius: 5px;
    /*pointer-events:none;*/
}

#navContainer .navBlock .navItem.active.dark{
    background: #163b74;
    color: #5E9CFF;
}

.hide{
    display: inline;
}


@media screen and (max-width: 1200px){

    #navContainer .navBlock .navItem{
        font-size: 12px;
    }

}

@media screen and (max-width: 1100px){
    #navContainer {
        padding-top: 25px;
    }
}

@media screen and (max-width: 960px){
    #navContainer .navBlock .navItem svg{
        margin-right: 8px;
    }


    #navContainer .navBlock .navItem{
        padding: 7px 0;
    }
}

@media screen and (max-width: 859px){
     #navContainer .navBlock .navItem{
        padding: 7px 10px;
    }

    #navContainer .navBlock{
        padding-top: 0;
        align-items: center;
    }
}

@media screen and (max-width: 490px){
    #navContainer .navBlock .hideMargin svg{
        margin: 0;
    }
    .hide{
        display: none;
    }
}

descriptor.controllerScript:
let userService;

let navItems = [{
        icon: 'space_dashboard',
        name: 'monitoring',
        title: 'Monitoring',
        active: false
    },
    {
        icon: 'pin_drop',
        name: 'locations',
        title: 'Locations',
        active: false, 
    },
    {
        icon: 'build',
        name: 'assets',
        title: 'Assets',
        active: false, 
    },
    {
        icon: 'memory',
        name: 'devices',
        title: 'Devices',
        active: false, 
    },
    {
        icon: 'settings',
        name: 'deviceConfigs',
        title: 'Device configs',
        active: false, 
    },
    {
        icon: 'people',
        name: 'users',
        title: 'Users',
        active: false, 
    },
    {
        icon: 'warning',
        name: 'alertsRules',
        title: 'Alerts rules',
        active: false, 
    }
];

let whiteLabelingService, params, controller;


self.onInit = function() {
    userService = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get("userService"));
    self.ctx.$scope.init = false;
    controller = self.ctx.$scope.ctx.stateController;

    whiteLabelingService = self.ctx.dashboard.whiteLabelingService;
    self.ctx.$scope.logoImg = whiteLabelingService.logoImageUrl();
    self.ctx.$scope.isDark = false;
    self.ctx.widget.config.backgroundColor = '#fff';
    self.ctx.updateWidgetParams();

    self.ctx.$scope.navItems = navItems;
    initParams();

    userService.getUserInfo(self.ctx.currentUser.userId).subscribe(response => {
        localStorage.setItem("role", response.groups.some((element) => element.name === "Tenant Administrators") ? "Admin" : "Viewer");
    });

    self.ctx.$scope.onNavClick = function(event, actionName) {
        initParams();
        let actionEventDescriptors = self.ctx.$scope.ctx.actionsApi.getActionDescriptors('elementClick');
        let actionEvent;

        setParams('stateId', actionName);
        setActiveMenu(actionName);

        if (actionEventDescriptors.length) {	
            actionEvent = actionEventDescriptors.find(action=>action.name == actionName);

            setTimeout(() => {
                self.ctx.$scope.ctx.actionsApi.handleWidgetAction(null, actionEvent, null, null);	
            }, 0);	
        }
    }

    self.ctx.detectChanges();
}

function updateParams(label, value){
    initParams();
    if(controller){
        params[label] = value; 
        controller.updateState(controller.stateValue, params)
    }
    self.ctx.detectChanges();
}

function initParams(){
    if(controller){
        params = controller.getStateParams();
    }
}

function setParams(label, value){
    params[label] = value;
}

function setMenu(){
    setActiveMenu();
    self.ctx.detectChanges();
}

function checkState(stateId){
    return self.ctx.$scope.navItems.find(item=>item.name==stateId);
}

function setActiveMenu(name=null){
    if(controller){
        initParams();
        let stateId = checkState(params['stateId']) ? params['stateId'] : params['prevState'];
        if(stateId){
            if(!name || name!=stateId){
                name = stateId;
            }
        }else{
            name = self.ctx.$scope.navItems[0].name;
            setHomePage(name);
        }
    }

    self.ctx.$scope.navItems.forEach(item=>{
        if(item.name==name){
            item.active =true;
        }else{
            item.active = false;
        } 
    });
    self.ctx.detectChanges(); 
}

function setHomePage(name){
    initParams();
    setParams('stateId', name);

    controller.updateState('default', params)
}

self.onDataUpdated = function() {
    let params = self.ctx.stateController.getStateParams();

    let stateToMakeEnabled = "default";
    if(params.statesArray){
        console.log("yeah, we have", params);
        stateToMakeEnabled = params.statesArray[0].stateId;
    } else {
        console.log("no", params)
        stateToMakeEnabled = params.stateId;
    }

    setMenu();

    if(!self.ctx.$scope.init){
        self.ctx.$scope.init = true;
    }

    self.ctx.detectChanges();
}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.typeParameters = function () {
    return {
        datasourcesOptional: true,
        dataKeysOptional: true
    };
}


self.onResize = function() {

}

self.onDestroy = function() {
    self.ctx.updateWidgetParams();
}

