descriptor.templateCss:
.leaflet-zoom-box {
	z-index: 9;
}

.leaflet-pane         { z-index: 4; }

.leaflet-tile-pane    { z-index: 2; }
.leaflet-overlay-pane { z-index: 4; }
.leaflet-shadow-pane  { z-index: 5; }
.leaflet-marker-pane  { z-index: 6; }
.leaflet-tooltip-pane   { z-index: 7; }
.leaflet-popup-pane   { z-index: 8; }

.leaflet-map-pane canvas { z-index: 1; }
.leaflet-map-pane svg    { z-index: 2; }

.leaflet-control {
	z-index: 9;
}
.leaflet-top,
.leaflet-bottom {
	z-index: 11;
}

.tb-marker-label {
    border: none;
    background: none;
    box-shadow: none;
}

.tb-marker-label:before {
    border: none;
    background: none;
}

descriptor.controllerScript:
self.onInit = function() {
    let $scope = self.ctx.$scope;
    $scope.widgetText = 'Ready to get data';
    self.ctx.broadcastService = $scope.$injector.get(self.ctx.servicesMap.get('broadcastService'));
    self.ctx.broadcastService.on('geofencesUpdate', (event, args) => {
        self.ctx.updateAliases();
        self.ctx.detectChanges();
    });
    self.ctx.map = new TbMapWidgetV2('openstreet-map', false, self.ctx);
}

self.onDataUpdated = function() {
    self.ctx.map.update();
}

self.onResize = function() {
    self.ctx.map.resize();
}

self.actionSources = function() {
    return TbMapWidgetV2.actionSources();
}

self.onDestroy = function() {
    self.ctx.map.destroy();
}

self.typeParameters = function() {
    return {
        hasDataPageLink: true
    };
}

