descriptor.controllerScript:
//Alias settings

// Required minimum 1 alias with the next keys:
// - perimeter (polygon)
// - color (attribute, can be null)

let utils, settings;

self.onInit = function() {
    attributeService = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('attributeService'));
    utils = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};

    self.ctx.map = new TbMapWidgetV2('openstreet-map', false, self.ctx);

    self.ctx.$scope.priorityPolygonKeyName = settings.priorityPolygonKeyName;
    self.ctx.$scope.priorityPolygonAliasName = settings.priorityPolygonAliasName;
    self.ctx.$scope.layers = [];
};

self.onDataUpdated = function() {
    if(self.ctx.$scope.layers.length>0){
        self.ctx.$scope.layers.forEach(x=>{
        	self.ctx.map.map.map.removeLayer(x); 
        });
    }

    self.ctx.datasources.forEach(x=>{
        if(x.aliasName == settings.priorityPolygonAliasName){
            let obj = {
                name: x.entityName
            };
            self.ctx.data.forEach(y=>{
                if(y.datasource.entityId == x.entityId){
                    if(y.data[0]){
                        obj[y.dataKey.label] = y.data[0][1];
                    }
                }
            });
            if(obj && obj[settings.priorityPolygonKeyName] && obj[settings.priorityPolygonColorKeyName]){
                let coordinates = JSON.parse(obj[settings.priorityPolygonKeyName]);
                drawPolygon(self.ctx.map.map.map, [coordinates], obj[settings.priorityPolygonColorKeyName]);
            }
        }
    });

    self.ctx.map.update();
};

function drawPolygon(map, coordinates, color){
    const polygon = L.polygon(coordinates, {
        color: color,
        fillColor: color,
        fillOpacity: 0.5,
        zIndex: 200
    }).addTo(map);

    self.ctx.$scope.layers.push(polygon);
}

self.onResize = function() {
    self.ctx.map.resize();
};

self.actionSources = function() {
    return TbMapWidgetV2.actionSources();
};

self.onDestroy = function() {
    self.ctx.map.destroy();
};

self.typeParameters = function() {
    return {
        hasDataPageLink: true
    };
};

