descriptor.controllerScript:
self.onInit = function() {
    utils = self.ctx.$scope.$injector.get(self.ctx.servicesMap.get('utils'));
    settings = utils.deepClone(self.ctx.settings) || {};

    self.ctx.$scope.title = settings.title;
    self.ctx.$scope.imgUrl = settings.imgUrl;

    self.ctx.$scope.value = "40.1";
    self.ctx.$scope.unit = "Â°N";
    self.ctx.$scope.time = "2 min ago";
}    

self.onDataUpdated = function() {
    self.ctx.$scope.value = [];

    self.ctx.data.forEach(x=>{
        if(x.data[0]){
            if(x.data[0]){
                self.ctx.$scope.unit = x.dataKey.units || '' 

                if(x.data[0][1] != ""){
                    self.ctx.$scope.value = x.data[0][1];
                } else {
                    self.ctx.$scope.value = 0;
                }

                self.ctx.$scope.time = x.data[0][0] == 0 ? "No updates" : moment(x.data[0][0]).fromNow();
            }
        }
    })

    self.ctx.detectChanges();
}

self.actionSources = function() {
    return {
        'elementClick': {
            name: 'widget-action.element-click',
            multiple: true
        }
    };
}

self.onResize = function() {

}

self.onDestroy = function() {
}

